const USDollar = new Intl.NumberFormat("en-US", {
  style: "currency",
  currency: "USD",
  //   maximumSignificantDigits: 3,
});
const USDollarWithoutCurrencySymbol = new Intl.NumberFormat("en-US", {
  //   style: "currency",
  currency: "USD",
  //   maximumSignificantDigits: 3,
});
export const formatCurrency = (amount, showCurrency) => {
  return [showCurrency ? `USDollarWithoutCurrencySymbol` : `USDollar`].format(
    amount
  );
};
