export const totalAmount = (loan_amount, product_interest) => {
  return loan_amount + loan_amount * parseFloat(product_interest);
};

export const monthlyInstallment = (
  loan_amount,
  num_of_months,
  product_interest
) => {
  // return totalAmount(loan_amount, product_interest) / num_of_months;
  return loan_amount / parseInt(num_of_months);
};

export const targetMonth = (current_month, num_of_months) => {
  return current_month + num_of_months;
};
