import React from "react";
import "./styles.css";
import productJson from "../../data/products.json";
import DollarSvg from "../../assets/dollar.svg";
import ArrowRightIcon from "../../assets/arrowright.svg";
import ArrowLeftIcon from "../../assets/arrowleft.svg";

export default class LoanMainCard extends React.Component {
  state = {
    loan_amount: null,
    num_of_months: 12,
  };
  render() {
    return (
      <div className="bodycard">
        <p className="heading-text">
          Let's plan your <strong>loan.</strong>
        </p>

        <div className="card">
          <div>
            {productJson.map((item) => (
              <img
                className="button-icon"
                src={item.image}
                key={item.id}
                alt={item.name}
              />
            ))}
          </div>
          <div className="input-container">
            <div className="form-feild loan-amount-feild">
              <p className="label">Loan amount</p>
              <div className="input-feild">
                <img alt="dollar" src={DollarSvg} />
                <input className="input-feild-box loan-input" />
              </div>
            </div>
            <div className="form-feild">
              <p className="label">Number of Months</p>
              <div className="input-feild">
                <img alt="arrowleft" src={ArrowLeftIcon} />

                <input className="input-feild-box month-input-feild" />
                <img alt="arrowright" src={ArrowRightIcon} />
              </div>
            </div>
          </div>
          <div className="message-display-card">
            <div className="monthly-card">
              <p className="monthly-amount-text">Monthly amount</p>
              <p className="monthly-card-text">$125</p>
            </div>
            <div className="monthly-down-card">
              <p className="monthly-down-text-card">
                Youâ€™re planning 12 <strong>monthly deposits</strong> to reach
                your <strong>$25,000</strong> goal by
                <strong>July 2022 </strong> . The total amount loaned will be
                <strong> $26,300</strong>
              </p>
            </div>
          </div>
          <div className="apply-now-button">
            <p className="apply-now-button-text">Apply Now</p>
          </div>
        </div>
      </div>
    );
  }
}
